(407, 1, '2021-02-18 13:00:34', '2021-02-18 12:00:34', '<!-- wp:image {\"id\":93,\"sizeSlug\":\"large\",\"linkDestination\":\"none\",\"className\":\"is-style-default\"} -->\n<figure class=\"wp-block-image size-large is-style-default\" id=\"accueil-img\"><img src=\"http://localhost:8888/Ardoise/wp-content/uploads/2021/01/carte-1024x683.jpg\" alt=\"\" class=\"wp-image-93\" title=\"image d\'accueil\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:nextpage -->\n<!--nextpage-->\n<!-- /wp:nextpage -->\n\n<!-- wp:media-text {\"mediaId\":61,\"mediaLink\":\"http://localhost:8888/Ardoise/2/\",\"mediaType\":\"image\"} -->\n<div class=\"wp-block-media-text alignwide is-stacked-on-mobile\"><figure class=\"wp-block-media-text__media\"><img src=\"http://localhost:8888/Ardoise/wp-content/uploads/2021/01/2-1024x718.png\" alt=\"\" class=\"wp-image-61 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:group -->\n<div id=\"presentationone\" class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:paragraph {\"align\":\"center\",\"placeholder\":\"Contenu…\",\"fontSize\":\"large\"} -->\n<p class=\"has-text-align-center has-large-font-size\">L\'ardoise, mais qu\'est ce que c\'est ? </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\" id=\"presentationone\">L\'ardoise est un restaurant situé au coeur du quartier d\'affaire de sofia Antipolis. Connu et reconnu pour ses produits frais en provenance d\'Italie, il ravit les papilles des différents salarié de cette zone économique. Le restaurant accueil les clients dans un cadre original et très attachant.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:nextpage -->\n<!--nextpage-->\n<!-- /wp:nextpage -->\n\n<!-- wp:media-text {\"mediaPosition\":\"right\",\"mediaId\":55,\"mediaLink\":\"http://localhost:8888/Ardoise/presentation__trashed/presentation/\",\"mediaType\":\"image\"} -->\n<div class=\"wp-block-media-text alignwide has-media-on-the-right is-stacked-on-mobile\"><figure class=\"wp-block-media-text__media\"><img src=\"http://localhost:8888/Ardoise/wp-content/uploads/2021/01/Presentation-1024x589.jpg\" alt=\"\" class=\"wp-image-55 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:group -->\n<div class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:group -->\n<div id=\"presentationtwo\" class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:paragraph {\"align\":\"center\",\"placeholder\":\"Contenu…\",\"fontSize\":\"large\"} -->\n<p class=\"has-text-align-center has-large-font-size\">Vous avez dis produit frais ?</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\" id=\"presentationtwo\">Oui nos produits sont frais et paré pour ravir vos papilles ! Le restaurant travail avec des fournisseur d\'Italie ainsi que certains de la zone de Sofia. C\'est dans cette idée de garantir des produits frais et de la bonne cuisine que ce restaurant a fait sa renommé</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:nextpage -->\n<!--nextpage-->\n<!-- /wp:nextpage -->\n\n<!-- wp:group -->\n<div id=\"groupcart\" class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:group -->\n<div id=\"groupcart\" class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:heading {\"textAlign\":\"center\"} -->\n<h2 class=\"has-text-align-center\">Mais que y\'a-t-il donc dans cette carte !</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">Patience patience ! Impatient ! Je te laisse choisir la citation et le plat qui te convient afin que tu puisse découvrir notre carte</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\" id=\"colonne-carte\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"textAlign\":\"center\",\"level\":3} -->\n<h3 class=\"has-text-align-center\"><a href=\"#\" data-type=\"URL\" target=\"_blank\" rel=\"noreferrer noopener\">L’argent ne fais pas le bonheur le saucisson oui !&nbsp;<br>- Entrée -</a></h3>\n<!-- /wp:heading --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"textAlign\":\"center\",\"level\":3} -->\n<h3 class=\"has-text-align-center\"><a href=\"#\" target=\"_blank\" rel=\"noreferrer noopener\">Si les fringales nocturnes sont vraiment proscrites alors pourquoi y a-t-il une lumière dans le frigo ?&nbsp;<br>- Plat -</a></h3>\n<!-- /wp:heading --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"textAlign\":\"center\",\"level\":3} -->\n<h3 class=\"has-text-align-center\"><a href=\"#\" target=\"_blank\" rel=\"noreferrer noopener\">Plus tu es lourd, plus tu es difficile à kidnapper.&nbsp;<br>Protège-toi.&nbsp;<br>Mange du gâteau.&nbsp;<br>- Dessert -</a></h3>\n<!-- /wp:heading --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"textAlign\":\"center\",\"level\":3} -->\n<h3 class=\"has-text-align-center\"><a href=\"#\" target=\"_blank\" rel=\"noreferrer noopener\">En attendant,ceux qui cherchent midi à 14 heures, ratent l’apéro&nbsp;<br>- Boisson -</a></h3>\n<!-- /wp:heading --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:group -->\n\n<!-- wp:nextpage -->\n<!--nextpage-->\n<!-- /wp:nextpage -->\n\n<!-- wp:group -->\n<div id=\"containerevent\" class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:heading {\"textAlign\":\"center\"} -->\n<h2 class=\"has-text-align-center\">La meilleure ambiance de la zone de Sofia Antipolis ? </h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">Que serait un bon repas sans une bonne ambiance ? N\'avons toujours pas besoin de décompresser de notre travail ? Ne vous inquiétez pas non sommes là !</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\" id=\"groupevent\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:shortcode -->\n[wpcdt-countdown id=\"108\"]\n<!-- /wp:shortcode --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"id\":109,\"width\":1024,\"sizeSlug\":\"large\",\"linkDestination\":\"none\",\"className\":\"is-style-default\"} -->\n<figure class=\"wp-block-image size-large is-resized is-style-default\"><img src=\"http://localhost:8888/Ardoise/wp-content/uploads/2021/02/board-953154_1920-1024x683.jpg\" alt=\"\" class=\"wp-image-109\" width=\"1024\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div></div>\n<!-- /wp:group -->\n\n<!-- wp:nextpage -->\n<!--nextpage-->\n<!-- /wp:nextpage -->\n\n<!-- wp:group -->\n<div class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:heading {\"textAlign\":\"center\"} -->\n<h2 class=\"has-text-align-center\">Vous souhaitez avoir de plus amples informations ? Vous pouvez nous contacter !</h2>\n<!-- /wp:heading -->\n\n<!-- wp:shortcode -->\n[contact-form-7 id=\"94\" title=\"Contact Ardoise\"]\n<!-- /wp:shortcode --></div></div>\n<!-- /wp:group -->', 'Accueil', '', 'inherit', 'closed', 'closed', '', '210-revision-v1', '', '', '2021-02-18 13:00:34', '2021-02-18 12:00:34', '', 210, 'http://localhost:8888/Ardoise/210-revision-v1/', 0, 'revision', '', 0),
