(576, 1, '2021-02-25 10:45:36', '2021-02-25 09:45:36', '<!-- wp:group -->\n<div class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:shortcode -->\n[smartslider3 slider=\"7\"]\n<!-- /wp:shortcode -->\n\n<!-- wp:heading {\"textAlign\":\"center\"} -->\n<h2 class=\"has-text-align-center\">RESTAURANT LOUNGE</h2>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"textAlign\":\"center\"} -->\n<h2 class=\"has-text-align-center\" id=\"Titlepres\">L\'Artdoise Craie L\'histoire</h2>\n<!-- /wp:heading --></div></div>\n<!-- /wp:group -->\n\n<!-- wp:group -->\n<div id=\"infpres\" class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\" id=\"infoprestop\">Espace Saint Philippe Sofia Antipolis / 13 avenue Roumanille 06410 Biot</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">Lundi - Vendredi 11H30-15H00 / 17H30-0H00</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">04.92.90.10.88</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:group -->\n\n<!-- wp:nextpage -->\n<!--nextpage-->\n<!-- /wp:nextpage -->\n\n<!-- wp:media-text {\"mediaId\":460,\"mediaLink\":\"http://localhost:8888/Ardoise/acceuil/a9335b2d-9bbd-4600-b48b-af6e8472f211/\",\"mediaType\":\"image\",\"mediaSizeSlug\":\"full\"} -->\n<div class=\"wp-block-media-text alignwide is-stacked-on-mobile\" id=\"grouppresentationone\"><figure class=\"wp-block-media-text__media\"><img src=\"http://localhost:8888/Ardoise/wp-content/uploads/2021/02/a9335b2d-9bbd-4600-b48b-af6e8472f211.jpg\" alt=\"\" class=\"wp-image-460 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:html -->\n<div class=\" animated slideInRight duration1 eds-on-scroll\"><hr></div>\n<!-- /wp:html -->\n\n<!-- wp:group -->\n<div id=\"presentationone\" class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:group -->\n<div class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:group -->\n<div id=\"grouppresentationone\" class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:group {\"className\":\"wow-slideInRight\"} -->\n<div class=\"wp-block-group wow-slideInRight\"><div class=\"wp-block-group__inner-container\"><!-- wp:group -->\n<div class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:group -->\n<div class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:group {\"className\":\"animated backInRight\"} -->\n<div class=\"wp-block-group animated backInRight\"><div class=\"wp-block-group__inner-container\"><!-- wp:group {\"className\":\"animated slideInRight duration1 eds-on-scroll\"} -->\n<div class=\"wp-block-group animated slideInRight duration1 eds-on-scroll\"><div class=\"wp-block-group__inner-container\"><!-- wp:paragraph {\"align\":\"center\",\"placeholder\":\"Contenu…\",\"fontSize\":\"large\"} -->\n<p class=\"has-text-align-center has-large-font-size\">L\'ardoise, mais qu\'est ce que c\'est ? </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\" id=\"presentationone\">L\'ardoise est un restaurant situé au coeur du quartier d\'affaire de sofia Antipolis. Connu et reconnu pour ses produits frais en provenance d\'Italie, il ravit les papilles des différents salarié de cette zone économique. Le restaurant accueil les clients dans un cadre original et très attachant.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:group -->\n\n<!-- wp:html -->\n<div class=\" animated slideInRight duration1 eds-on-scroll\"><br><br><br><hr></div>\n<!-- /wp:html --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:nextpage -->\n<!--nextpage-->\n<!-- /wp:nextpage -->\n\n<!-- wp:media-text {\"mediaPosition\":\"right\",\"mediaId\":462,\"mediaLink\":\"http://localhost:8888/Ardoise/acceuil/ae7601af-8100-48e4-8996-55091dc2b367/\",\"mediaType\":\"image\"} -->\n<div class=\"wp-block-media-text alignwide has-media-on-the-right is-stacked-on-mobile\"><figure class=\"wp-block-media-text__media\"><img src=\"http://localhost:8888/Ardoise/wp-content/uploads/2021/02/ae7601af-8100-48e4-8996-55091dc2b367.jpg\" alt=\"\" class=\"wp-image-462 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:html -->\n<div class=\"animated slideInLeft duration1 eds-on-scroll \"><hr></div>\n<!-- /wp:html -->\n\n<!-- wp:group -->\n<div class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:group -->\n<div id=\"presentationtwo\" class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:group {\"className\":\"animated slideInLeft duration1 eds-on-scroll\"} -->\n<div class=\"wp-block-group animated slideInLeft duration1 eds-on-scroll\"><div class=\"wp-block-group__inner-container\"><!-- wp:paragraph {\"align\":\"center\",\"placeholder\":\"Contenu…\",\"fontSize\":\"large\"} -->\n<p class=\"has-text-align-center has-large-font-size\">Vous avez dis produit frais ?</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\" id=\"presentationtwo\">Oui nos produits sont frais et paré pour ravir vos papilles ! Le restaurant travail avec des fournisseur d\'Italie ainsi que certains de la zone de Sofia. C\'est dans cette idée de garantir des produits frais et de la bonne cuisine que ce restaurant a fait sa renommé</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:group -->\n\n<!-- wp:html -->\n<div class=\"animated slideInLeft duration1 eds-on-scroll \"><br><br><br><hr></div>\n<!-- /wp:html --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:nextpage -->\n<!--nextpage-->\n<!-- /wp:nextpage -->\n\n<!-- wp:group -->\n<div id=\"groupcart\" class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:group -->\n<div id=\"groupcart\" class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:heading {\"textAlign\":\"center\"} -->\n<h2 class=\"has-text-align-center\">Mais que y\'a-t-il donc dans cette carte !</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">Patience patience ! Impatient ! Je te laisse choisir la citation et le plat qui te convient afin que tu puisse découvrir notre carte</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\" id=\"colonne-carte\"></div>\n<!-- /wp:columns --></div></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:group -->\n\n<!-- wp:shortcode -->\n[erm_menu id=518]\n<!-- /wp:shortcode -->\n\n<!-- wp:nextpage -->\n<!--nextpage-->\n<!-- /wp:nextpage -->\n\n<!-- wp:group -->\n<div id=\"containerevent\" class=\"wp-block-group\"><div class=\"wp-block-group__inner-container\"><!-- wp:heading {\"textAlign\":\"center\"} -->\n<h2 class=\"has-text-align-center\">La meilleure ambiance de la zone de Sofia Antipolis ? </h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">Que serait un bon repas sans une bonne ambiance ? N\'avons toujours pas besoin de décompresser de notre travail ? Ne vous inquiétez pas non sommes là !</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\" id=\"groupevent\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:shortcode -->\n[wpcdt-countdown id=\"108\"]\n<!-- /wp:shortcode --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"id\":109,\"width\":1024,\"sizeSlug\":\"large\",\"linkDestination\":\"none\",\"className\":\"is-style-default\"} -->\n<figure class=\"wp-block-image size-large is-resized is-style-default\"><img src=\"http://localhost:8888/Ardoise/wp-content/uploads/2021/02/board-953154_1920-1024x683.jpg\" alt=\"\" class=\"wp-image-109\" width=\"1024\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div></div>\n<!-- /wp:group -->', 'Accueil', '', 'inherit', 'closed', 'closed', '', '210-revision-v1', '', '', '2021-02-25 10:45:36', '2021-02-25 09:45:36', '', 210, 'http://localhost:8888/Ardoise/210-revision-v1/', 0, 'revision', '', 0),
